mapper.select:
SELECT f.folder_name as folderName, f.aging_day as keepDays 
FROM mail_userfolder_aging f 
JOIN mail_user u ON f.mail_user_seq = u.mail_user_seq 
JOIN mail_domain d ON u.mail_domain_seq = d.mail_domain_seq 
WHERE u.mail_uid = #{userId} 
AND d.mail_domain = #{domain} 
AND f.folder_name = #{folderName}

mapper.update:
UPDATE mail_userfolder_aging 
SET aging_day = #{keepDays} 
WHERE folder_name = #{folderName} 
AND mail_user_seq = (
  SELECT u.mail_user_seq FROM mail_user u 
  JOIN mail_domain d ON u.mail_domain_seq = d.mail_domain_seq 
  WHERE u.mail_uid = #{userId} AND d.mail_domain = #{domain} 
)

UPDATE mail_user_info SET ${columnName} = #{keepDays} 
WHERE mail_user_seq = (
  SELECT u.mail_user_seq FROM mail_user u 
  JOIN mail_domain d ON u.mail_domain_seq = d.mail_domain_seq 
  WHERE u.mail_uid = #{userId} AND d.mail_domain = #{domain} 
)

mapper.delete:
DELETE FROM mail_userfolder_aging 
WHERE folder_name = #{folderName} 
AND mail_user_seq = (
  SELECT u.mail_user_seq FROM mail_user u 
  JOIN mail_domain d ON u.mail_domain_seq = d.mail_domain_seq 
  WHERE u.mail_uid = #{userId} AND d.mail_domain = #{domain} 
)
