sqlMap.select:
SELECT zz.name,zz.email FROM( SELECT a.member_name as name, a.member_email as email FROM adr_private_member a WHERE a.mail_user_seq=#userSeq# and (a.delete_flag IS NULL or a.delete_flag != 'Y')
member_email like '$keyWord$%')
and member_name = #keyWord#
) zz LEFT JOIN( SELECT mail_uid as name,CONCAT(mail_uid,CONCAT('@',b.mail_domain)) as email FROM mail_user a, mail_domain b where a.mail_domain_seq = b.mail_domain_seq and a.mail_domain_seq = #mailDomain# and a.account_status = 'disabled' ) gg ON zz.email = gg.email WHERE gg.email is null;

SELECT zz.name,zz.email FROM( SELECT b.member_name as name, b.member_email as email FROM adr_shared_master a, adr_shared_member b WHERE a.mail_domain_seq=#mailDomain# and a.adrbook_seq=b.adrbook_seq and b.adrbook_seq in ( select adrbook_seq from adr_shared_reader where mail_user_seq=#userSeq# union select adrbook_seq from adr_shared_moderator where mail_user_seq=#userSeq# union select adrbook_seq from adr_shared_master where creator_seq = #userSeq# or reader_type='A' )
member_email like '$keyWord$%')
and member_name = #keyWord#
) zz LEFT JOIN( SELECT mail_uid as name,CONCAT(mail_uid,CONCAT('@',b.mail_domain)) as email FROM mail_user a, mail_domain b where a.mail_domain_seq = b.mail_domain_seq and a.mail_domain_seq = #mailDomain# and a.account_status = 'disabled' ) gg ON zz.email = gg.email WHERE gg.email is null;

SELECT zz.name,zz.email FROM( select a.member_name as name, a.lastrcpt_email as email from (select mail_user_seq, lastrcpt_email, lastrcpt_personnel as member_name from wcf_lastrcpt where mail_user_seq = #userSeq#) a where a.mail_user_seq = #userSeq#
lastrcpt_email like '$keyWord$%')
and member_name = #keyWord#
) zz LEFT JOIN( SELECT mail_uid as name,CONCAT(mail_uid,CONCAT('@',b.mail_domain)) as email FROM mail_user a, mail_domain b where a.mail_domain_seq = b.mail_domain_seq and a.mail_domain_seq = #mailDomain# and a.account_status = 'disabled' ) gg ON zz.email = gg.email WHERE gg.email is null;
